import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import TableSortLabel from '@material-ui/core/TableSortLabel';
import Checkbox from '@material-ui/core/Checkbox';
import clsx from 'clsx';
import PropTypes from 'prop-types';
import {lighten} from '@material-ui/core/styles';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import Tooltip from '@material-ui/core/Tooltip';
import DeleteIcon from '@material-ui/icons/Delete';
import FilterListIcon from '@material-ui/icons/FilterList';
import CheckIcon from '@material-ui/icons/Check';
import axios from 'axios';

function createData(company, location, position, year, wage, user, pID) {
  return { company, location, position, year, wage, user, pID};
}

var itemData = []

const deleteBuffer = async (post) => {
  post.event.preventDefault();
  const deleteUrl = "http://67.159.88.90:8000/api/wagebuffers/"

  for(let i = 0; i < post.items.length; i++) {
    let itemID = post.items[i]
    var item = {}
    itemData.forEach(function (dataItem, itemIndex) {
      if(dataItem.pID === itemID) {
        item = dataItem
      }
    })
    axios.delete(deleteUrl + String(item.pID),{
      headers: {
        "Authorization": "Token " + localStorage.getItem("key"),
        "Content-Type": "application/json",
      }
    }).then(res => {
      if(i === post.items.length - 1) {
        window.location.reload(false)
      }
    })
  }
}

const approveBuffer = async (post) => {
  post.event.preventDefault();
  const deleteUrl = "http://67.159.88.90:8000/api/wagebuffers/"
  const postUrl = "http://67.159.88.90:8000/api/wagepostings/"

  for(let i = 0; i < post.items.length; i++) {
    let itemID = post.items[i]
    var item = {}
    itemData.forEach(function (dataItem, itemIndex) {
      if(dataItem.pID === itemID) {
        item = dataItem
      }
    })
    axios.delete(deleteUrl + String(item.pID),{
      headers: {
        "Authorization": "Token " + localStorage.getItem("key"),
        "Content-Type": "application/json",
      }
    }).then(res => {
      const data = {
        "siteid": item.location,
        "employerid": item.company,
        "uid": item.user,
        "position": item.position,
        "wage": item.wage,
        "year": item.year,
      };
      axios.post(postUrl, data, {
          headers: {
            "Authorization": "Token " + localStorage.getItem("key"),
            "Content-Type": "application/json",
          }
        }).then(res => {
          if(i === post.items.length - 1) {
            window.location.reload(false)
          }
        })
        .catch(err => console.log(err));
    }).catch(err => console.log(err));
  }
}

function desc(a, b, orderBy) {
  if (b[orderBy] < a[orderBy]) {
    return -1;
  }
  if (b[orderBy] > a[orderBy]) {
    return 1;
  }
  return 0;
}

function stableSort(array, cmp) {
  const stabilizedThis = array.map((el, index) => [el, index]);
  stabilizedThis.sort((a, b) => {
    const order = cmp(a[0], b[0]);
    if (order !== 0) return order;
    return a[1] - b[1];
  });
  return stabilizedThis.map(el => el[0]);
}

function getSorting(order, orderBy) {
  return order === 'desc' ? (a, b) => desc(a, b, orderBy) : (a, b) => -desc(a, b, orderBy);
}

const headCells = [
  { id: 'company', numeric: false, disablePadding: true, label: 'Company' },
  { id: 'location', numeric: true, disablePadding: false, label: 'Location' },
